                    border: 0;
                }
            }

            &:nth-child(1),
            &:nth-child(4) {
                padding-left: 20%;

                @include media("<=tablet") {
                    padding-left: unset;
                }
            }

            &:nth-child(2),
            &:nth-child(3) {
                padding-left: 25%;

                @include media("<=tablet") {
                    padding-left: unset;
                }
            }

