                    padding-left: 10%;
                    border-right: 0.8px solid $creamy-brown;

                    &:not(:nth-child(3)) {
                        border-bottom: 0.8px solid $creamy-brown;
                    }
                }
            }

            &:nth-child(2),
            &:nth-child(4) {
                @include media("<=tablet") {
                    padding-left: 15%;

                    &:not(:nth-child(4)) {
                        border-bottom: 0.8px solid $creamy-brown;
                    }
                }
            }
            // Mobile

            & li {
                & h3 {
                    font-family: "Athletics";
                    font-weight: 500;
                    font-size: 32px;
                    line-height: 80px;
                    color: $white;
                    text-transform: uppercase;

                    @include media("<=tablet") {
                        font-weight: 400;
                        font-size: 20px;
                        line-height: 45px;
                    }
                }

                & a {
                    font-family: "Athletics";
                    font-weight: 400;
                    font-size: 24px;
                    line-height: 60px;
                    color: $white;
                    text-decoration: none;
                    text-transform: capitalize;

                    @include media("<=tablet") {
                        font-size: 16px;
                        line-height: 40px;
                    }
                }
            }
        }
    }

