        width: 100%;
        height: fit-content;
        display: flex;
        align-items: center;
        flex-direction: column;
        padding: 90px 0 0;

        @include media("<=tablet") {
            padding: 40px 0 0;
        }

        &--text {
            width: max-content;
            height: fit-content;
            margin-bottom: 60px;

            @include media("<=tablet") {
                margin-bottom: 40px;
            }

            & h3 {
                font-weight: 400;
                font-size: 64px;
                line-height: 81px;
                text-align: center;
                color: $white;

                @include media("<=tablet") {
                    font-size: 36.6452px;
                    line-height: 46px;
                }
            }
        }

        &--icons {
            display: flex;
            align-items: center;
            justify-content: center;
            height: fit-content;
            width: max-content;
